//accion del boton buscar, del cual adquiere el campo de fecha
    private void search_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_buttonActionPerformed
        String p1 = null;
        String[][] qryRes = null;
        String []data = new String[8];
        p1 = ((JTextField)date_chooser.getDateEditor().getUiComponent()).getText();
        DefaultTableModel model = new DefaultTableModel();
        model.addColumn("Id registro");
        model.addColumn("Fecha analisis");
        model.addColumn("Clave");
        model.addColumn("Fecha producci√≥n");
        model.addColumn("#Cocinada");
        model.addColumn("Final Stat");
        model.addColumn("Operador");
        model.addColumn("Id usuario");
        table1.setModel(model);
        table1.setEnabled(false);
        
        if(p1!= null){
            LogConexion buscar = new LogConexion();
            buscar.open();
            qryRes = buscar.getReg(p1);
            buscar.close(); 
            
            for(int i=0; i<=qryRes.length;i++){
                for(int j=0; j<8; j++){
                    data[j]=qryRes[j][i];
                }
                model.addRow(data);
            }  
        }
    }//GEN-LAST:event_search_buttonActionPerformed

